import React, { useState } from 'react';
import Select from 'react-select';

function IngredientInput() {
  const [customIngredient, setCustomIngredient] = useState('');
  const [selectedIngredients, setSelectedIngredients] = useState([]);
  const [highPriorityIngredients, setHighPriorityIngredients] = useState([]);
  const [mustNotHaveIngredients, setMustNotHaveIngredients] = useState([]);

  const handleCustomIngredientChange = (e) => {
    setCustomIngredient(e.target.value);
  };

  const handleAddCustomIngredient = () => {
    if (customIngredient) {
      setSelectedIngredients((prevIngredients) => [
        ...prevIngredients,
        { value: customIngredient, label: customIngredient },
      ]);
      setCustomIngredient('');
    }
  };

  const handlePriorityChange = (selectedOption) => {
    setHighPriorityIngredients(selectedOption);
  };

  const handleMustNotHaveChange = (selectedOption) => {
    setMustNotHaveIngredients(selectedOption);
  };

  return (
    <div>
      <label>Enter Priority Ingredient:</label>
      <input
        type="text"
        value={customIngredient}
        onChange={handleCustomIngredientChange}
      />
      <button onClick={handleAddCustomIngredient}>Add Ingredient</button>

      <label>Select High-Priority Ingredients:</label>
      <Select
        options={selectedIngredients}
        isMulti
        value={highPriorityIngredients}
        onChange={handlePriorityChange}
      />

      <label>Select Ingredients to Avoid (Must Not Have):</label>
      <Select
        options={selectedIngredients}
        isMulti
        value={mustNotHaveIngredients}
        onChange={handleMustNotHaveChange}
      />
    </div>
  );
}

export default IngredientInput;
