import React, { useState } from 'react';

function HighPriorityIngredients(props) {
  const {handleAddIngredient, type} = props;
  const [mustHaveIngredient, setMustHaveIngredient] = useState('');

  const handleHighPriorityChange = (e) => {
    setMustHaveIngredient(e.target.value);
  };

  const handleAddHighPriority = (e) => {
    handleAddIngredient(e, type, ingredient);
  }

  return (
    <div>
      <input
        type="text"
        value={highPriorityIngredient}
        onChange={handleHighPriorityChange}
        onKeyDown={(e) => handleAddIngredient ? (e.key === 'Enter' && mustHaveIngredient.trim() !== '') : null}
        placeholder="Enter a must include"
      />
      <button onClick={handleAddIngredient}>Add</button>
    </div>
  );
}

export default HighPriorityIngredients;
