import './App.css';
import React, { useState, useEffect } from "react";
import ImportForm from './ImportForm';
import DishSelect from './DishSelect';

function App() {
  const [response, setResponse] = useState("");
  const [dishes, setDishes] = useState({});
  const [loaded, setLoaded] = useState(false);
  const [loading, setLoading] = useState(false);
  const [selectedDish, setSelectedDish] = useState(null);

  return (
    /*
    <div className="App">
      {loading 
      ? <h1>loading... please wait</h1> 
      : loaded 
        ? <DishSelect dishes={dishes} setLoaded={setLoaded}/> 
        : <ImportForm setDishes={setDishes} setLoaded={setLoaded} setLoading={setLoading}/>}
    </div>
    */
    <div className="App">
      {selectedDish ? (
        <Recipe dish={selectedDish} setSelectedDish={setSelectedDish} />
      ) : loading ? (
        <h1>loading... please wait</h1>
      ) : loaded ? (
        <DishSelect dishes={dishes} setSelectedDish={setSelectedDish} />
      ) : (
        <ImportForm setDishes={setDishes} setLoaded={setLoaded} setLoading={setLoading} />
      )}
    </div>
  );
}

export default App;
