import React, { useState, useRef } from 'react';
import HighPriorityIngredients from './HighPriorityIngredients';
import MustNotIngredients from './MustNotIngredients';
import CanHaveIngredients from './CanHaveIngredients';

const mealTypeOptions = [
    "",
    "Breakfast",
    "Entree",
    "Snack/Appetizer",
    "Dessert",
  ];

const cookingTimeOptions = [
    "",
    "<25 min",
    "25-45 min",
    ">45 min"
]

function ImportForm() {
    const [ingredients, setIngredients] = useState({
      mustHave: [],
      cannotHave: [],
      canHave: [],
    });

    // const [mustHave, setMustHave] = useState('');
    // const [cannotHave, setCannotHave] = useState('');
    // const [canHave, setCanHave] = useState('');
    const [cookingTime, setCookingTime] = useState('');
    const [mealType, setMealType] = useState('');

    const highPriorityRef = useRef();
    const mustNotRef = useRef();
    const canHaveRef = useRef();

    const handleAddIngredient = (e, type, ingredient) => {
      if (!ingredients[type].includes(ingredient)) {
        setIngredients(prevIngredients => ({
            ...prevIngredients,
            [type]: [...prevIngredients[type], ingredient]
          }));
        }
    }

    const handleSubmit = (e) => {
      e.preventDefault();

      const recipeData = {
        ingredients: {
          mustHave: highPriorityRef.current,
          cannotHave: mustNotRef.current,
          canHave: canHaveRef.current,
        },
        cookingTime: cookingTime,
        mealType: mealType,
      };

      fetch('http://127.0.0.1:5000/add_prefs', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(recipeData),
      })
        //.then((response) => response.json())

        .then((response) => response.text())
        .then((data) => {
          console.log(data);
        })
        .catch((error) => {
          console.error('Error:', error);
        });
    };

    return (
      <form >
        <div>
          <h4>High-Priority Ingredients</h4>
          <HighPriorityIngredients ref={highPriorityRef} props={{handleAddIngredient: handleAddIngredient, type: "mustHave"}} />
          <ul>
            {ingredients["mustHave"].map((ingredient, index) => (
              <li key={index}>{ingredient}</li>
            ))}
          </ul>
        </div>
        <MustNotIngredients ref={mustNotRef} />
        <CanHaveIngredients ref={canHaveRef} />
        <br />
        <label>
          Cooking Time:
            <select value={mealType} onChange={(e) => setCookingTime(e.target.value)}>
            {cookingTimeOptions.map((option) => (
                <option key={option} value={option}>
                {option}
                </option>
            ))}
            </select>
        </label>
        <br />
        <label>
            Meal Type:
            <select value={mealType} onChange={(e) => setMealType(e.target.value)}>
            {mealTypeOptions.map((option) => (
                <option key={option} value={option}>
                {option}
                </option>
            ))}
            </select>
        </label>
        <br />
        <button onClick={handleSubmit}>
            Submit!
        </button>
        {/*<button type="submit">Add Preferences!</button>*/}
      </form>
    );
  }

  export default ImportForm;
